#!/bin/bash

# Script to check users on the server
# Run this on the server: bash check-users.sh

DB_PATH="/home/mmlipl/htdocs/mmlipl.in/server/tms_database.db"

echo "🔍 Checking users on server..."
echo ""

# Check if database exists
if [ ! -f "$DB_PATH" ]; then
    echo "❌ Database file not found at: $DB_PATH"
    exit 1
fi

# Count total users
TOTAL_COUNT=$(sqlite3 "$DB_PATH" "SELECT COUNT(*) FROM users;" 2>/dev/null)
echo "📊 Total Users: $TOTAL_COUNT"
echo ""

# Show all users
if [ "$TOTAL_COUNT" -gt 0 ]; then
